import { useState } from "react";
import "./style.css";

const initialComments = [
  {
    id: 1,
    text: "Dr. Gracie is the most compassionate mental health provider!",
    source: "patient",
    category: "psychiatry",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 0,
    createdIn: 2023,
  },
  {
    id: 2,
    text: "Dr. Sandeep. I wanted to express my deepest gratitude for the excellent care you provided during my recent treatment for my heart condition. Your expertise and professionalism were apparent from the very beginning, and I knew I was in good hands.",
    source: "patient",
    category: "cardiology",
    votesInteresting: 40,
    votesMindblowing: 4,
    votesFalse: 0,
    createdIn: 2023,
  },

  {
    id: 3,
    text: "A special thanks to John in IT for helping me navigate the EPIC software.",
    source: "employee",
    category: "information technology",
    votesInteresting: 9,
    votesMindblowing: 21,
    votesFalse: 0,
    createdIn: 2023,
  },
];

//COMPONENTS
function App() {
  const [showForm, setShowForm] = useState(false);
  return (
    <>
      {/* HEADER */}
      <Header showForm={showForm} setShowForm={setShowForm} />
      {showForm ? <NewPostForm /> : null}

      <main className="main-container">
        <CategoryFilter />
        <CategoryFilterMobile />
        <PostList />
      </main>
    </>
  );
}

function Header({ showForm, setShowForm }) {
  const APP_TITLE = "Priase";

  return (
    <header className="header">
      <div className="logo">
        <img src="logo.png" alt="heart logo" width="120px" height="120px" />
        <h1>{APP_TITLE}</h1>
      </div>

      <button
        className="head-btn btn-large"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share"}
      </button>
    </header>
  );
}

function NewPostForm() {
  return <form className="main-form">Post Form</form>;
}

const DEPARTMENTS = [
  { name: "All", class: "all" },
  { name: "Admissions", class: "admissions" },
  { name: "Anesthesiology", class: "anesthesiology" },
  { name: "Cardiology", class: "cardiology" },
  { name: "Emergency", class: "emergency" },
  { name: "Gastroenterology", class: "gastroenterology" },
  { name: "General Services", class: "general-services" },
  { name: "ICU", class: "icu" },
  { name: "IT", class: "it" },
  { name: "Neurology", class: "neurology" },
  { name: "Obstetrics", class: "obstetrics" },
  { name: "Oncology", class: "oncology" },
  { name: "Orthopedics", class: "orthopedics" },
  { name: "Pathology", class: "pathology" },
  { name: "Pharmacology", class: "pharmacology" },
  { name: "Psychiatry", class: "psychiatry" },
  { name: "Radiology", class: "radiology" },
  { name: "Surgery", class: "surgery" },
];

function CategoryFilter() {
  return (
    <aside>
      <ul className="aside-dep">
        {DEPARTMENTS.map((obj) => (
          <li key={obj.name}>
            <button className={`btn-d ${obj.class}`}>{obj.name}</button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function CategoryFilterMobile() {
  return (
    <select
      className="dep-btn-mobile btn-large hidden"
      name="category"
      id="category-mobile"
    >
      <option defaultValue="">Select a department</option>
      {DEPARTMENTS.map((obj) => (
        <option defaultValue={obj.class}>{obj.name}</option>
      ))}
    </select>
  );
}

function PostList() {
  const posts = initialComments;

  return (
    <section>
      <ul className="post-list">
        {posts.map((obj) => (
          <POST key={obj.id} postObj={obj} />
        ))}
      </ul>
      <p className="post-count">
        There {posts.length === 1 ? "is" : "are"} {posts.length}{" "}
        {posts.length === 1 ? "praise" : "praises"}. Share your own!
      </p>
    </section>
  );
}

function POST({ postObj }) {
  return (
    <li key={postObj.id} className="post-container">
      <p className="post">
        {postObj.text}
        <span className="who">- {postObj.source}</span>
      </p>
      <span className="department bubble">{postObj.category}</span>
      <div className="reactions">
        <button>❤️ 25</button>
        <button>🙌 15</button>
        <button>🙏 3</button>
      </div>
    </li>
  );
}
export default App;
